import {useParams}from 'react-router-dom';
import { useState, useEffect } from 'react';

export default function Detail({detailId}){
   const[character, setCharacter]=useState()
   
    const id= useParams()
    useEffect(() => {
        fetch(`https://rickandmortyapi.com/api/character/${detailId}`)
          .then((response) => response.json())
          .then((char) => {
            if (char.name) {
              setCharacter(char);
            } else {
              window.alert("No hay personajes con ese ID");
            }
          })
          .catch((err) => {
            window.alert("No hay personajes con ese ID");
          });
        return setCharacter({});
      }, [id]);

    return(
      <>
      <div className={style.containerCardBody}>
        <button className={style.close} onClick={onClose}>X</button>
        <div className={style.containerName}>
        <Link to={`/detail/${id}`}>
        <h2 className={style.name}>{name}</h2>
        </Link>
        </div>
        <div className={style.containerCardFooter}>
        <h2 className={style.gender}>{gender}</h2>
        <h2 className={style.specie}>{species}</h2>
        </div>
      </div>
      </>
    )
}
