import {  useState} from 'react'
import style from './Form.module.css'

const Form=(props)=>{

const[userData, setUserData]= useState({
    username: '',
    password: ''
   })

const [errors, setErrors]= useState({
    userName:'',
    password:''
})
  



const handleInputChange=(event)=>{
    setUserData({
        ...userData,  [event.target.name]:event.target.value
    }) 
    validation(event)
 }


 const validation = (event) => {
    if (!/\S+@\S+\.\S+/.test(event.target.value)) {
        setErrors("el usuario tiene que ser un email");
    }
    if (!event.target.value) {
      //userName
      setErrors("el nombre de usuario no puede estar vacío");
    }
    if (event.target.value.length > 35) {
      //userName
      setErrors("el nombre de usuario no puede tener más de 35 caracteres");
    }
    if (!/\d+/.test(event.target.value)) {
        setErrors("la contraseña tiene que tener al menos un número.");
    }
    if (event.target.value.length >= 6 && event.target.value.length <= 10) {
        setErrors("la contraseña tiene que tener una longitud entre 6 y 10 caracteres");
    }
  };
  


    return(
        <form className={style.containerForm}>
            <section>
            <label htmlFor="user">Username:</label>
            <input type="text" value={userData.username} name='username' onChange={handleInputChange} >{}</input>
            </section>
            <section>
            <label htmlFor="password">Password:</label>
            <input type="pasword" value={userData.password} name='password' onChange={handleInputChange}></input>
            </section>
            <button>Login</button>
        </form>
    )
}

export default Form
