import { connect, useDispatch } from "react-redux";
import style from '../card/Card.module.css'
import { Link } from "react-router-dom";
import { filterCards, orderCards } from "../../redux/actions";

const Favorite=({myFavorites})=>{
  const dispatch= useDispatch()
  
  const onHandlerOrder=(event)=>{
      dispatch(orderCards(event.target.value))
    }
    const onHandlerFilter=(event)=>{
      dispatch(filterCards(event.target.value))
  
    }


    return(
        <div>   

            <ul>
                <div className={style.favContainer}>
                 <select onChange={onHandlerOrder}>
                        <option value={"order"} disabled="disabled">Order By</option>
                        <option value="Ascendente">Ascendente</option>
                        <option value="Descedente">Descedente</option>
                </select>
                 <select onChange={onHandlerFilter}>
                         <option value={"filter"} disabled="disabled">Filter By</option>
                        <option value="Male"> Male</option>
                        <option value="Female">Female</option>
                        <option value="Genderless">Genderless</option>
                        <option value="Unknown">Unknown</option>
                  </select>
                </div>
            {
                myFavorites.map((favorite)=>{
                   return(<div className={style.cardContainer}>
                   <img className={style.img} src={favorite.image} alt="" /> 
                  <div className={style.containerCardBody}>
                   <div className={style.containerName}>
                     <Link to={`/detail/${favorite.id}`}>
                     <h2 className={style.name}>{favorite.name}</h2>
                     </Link>
                     </div>
                     <div className={style.containerCardFooter}>
                     <h2 className={style.gender}>{favorite.gender}</h2>
                     <h2 className={style.specie}>{favorite.species}</h2>
                     </div>
                  </div>
           
               </div>)
                })
            }
            </ul>
        </div>  
    )          
}


export const mapStateToProps=(state)=>{
    return{
        myFavorites: state.myFavorites
    }
}

export default connect(mapStateToProps,null)(Favorite)
