/* USERNAME
el nombre de usuario tiene que ser un email (explora validaciónes REGEX en internet!).
el nombre de usuario no puede estar vacío.
el nombre de usuario no puede tener más de 35 caracteres.


PASSWORD
la contraseña tiene que tener al menos un número.
la contraseña tiene que tener una longitud entre 6 y 10 caracteres. */

const validation = (value) => {
  if (!/\S+@\S+\.\S+/.test(value)) {
    alert("el usuario tiene que ser un email");
  }
  if (!value) {
    //userName
    alert("el nombre de usuario no puede estar vacío");
  }
  if (value.length > 35) {
    //userName
    alert("el nombre de usuario no puede tener más de 35 caracteres");
  }
  if (value.length >= 6 && value.length <= 10) {
    alert("la contraseña tiene que tener una longitud entre 6 y 10 caracteres");
  }
};

export default validation;
