import { connect } from 'react-redux';
import style from './Card.module.css'
import {Link} from 'react-router-dom'
import { addFavorite, removeFavorite } from '../../redux/actions';
import { useState, useEffect } from 'react';

export function Card({onClose, name, id, species,gender,image, deleteFavorite,addFavorite, myFavorites}) {
   
   const[isFav, setIsFav]= useState(false)
   const handleFavorite=()=>{
      if(isFav){
         setIsFav(false)
         deleteFavorite(id)
      }else { 
         setIsFav(true)
         
       const{addFavorite}=   addFavorite({name, id, species,gender,image})
      }
   }
   
   useEffect(() => {
      myFavorites?.forEach((favorite) => {
         if (favorite.id === id) {
            setIsFav(true);
         }
   });
}, [myFavorites]);
 
console.log(addFavorite)
return (
      <div className={style.cardContainer}>
         {
            isFav? 
               (<button onClick={handleFavorite}>‚ù§Ô∏è </button>)
            : (<button onClick={handleFavorite}>ü§ç </button>)   
         }
          <img className={style.img} src={image} alt="" /> 
         <div className={style.containerCardBody}>
            <button className={style.close} onClick={onClose}>X</button>
            <div className={style.containerName}>
            <Link to={`/detail/${id}`}>
            <h2 className={style.name}>{name}</h2>
            </Link>
            </div>
            <div className={style.containerCardFooter}>
            <h2 className={style.gender}>{gender}</h2>
            <h2 className={style.specie}>{species}</h2>
            </div>
         </div>
      </div>
   );
}

const mapDispatchToProps=(dispatch)=>{
   return {
      addFavorite: ({name, id, species,gender,image})=>{dispatch(addFavorite({name, id, species,gender,image}))},
      removeFavorite: (id)=>{dispatch(removeFavorite(id))}
   }
}

const mapStateToProps=(state)=>{
   return{
      myFavorites: state.myFavorites
   }
}

export default connect(mapStateToProps, mapDispatchToProps)(Card)
