import { connect } from 'react-redux';
import style from './Card.module.css'
import {Link} from 'react-router-dom'
import { addFavorite, removeFavorite } from '../../redux/actions'
import { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';

export function Card({onClose, name, id, species,gender,image, addFavorite, removeFavorite }) {
   
   const[isFav, setIsFav]= useState(false)
   const favorites= useSelector((state)=> state.myFavorites) //del stado completo traemee a favorites
   const dispatch= useDispatch()
   const handleFavorite=()=>{
      if(isFav){
         setIsFav(false)
         removeFavorite(id)
         
      }else { 
         setIsFav(true)
         addFavorite({...favorites,name, id, species,gender,image }) //es la funci칩n que recibo por prop NO es la action
      }
   }
   
   useEffect(() => {
      favorites?.forEach((favorite) => {
         if (favorite.id === id) {
            setIsFav(true);
         }
   });
}, [favorites]);
 
return (
      <div className={style.cardContainer}>
         {
            isFav? 
               (<button onClick={handleFavorite}>仇벒잺 </button>)
            : (<button onClick={handleFavorite}>游밼 </button>)   
         }
          <img className={style.img} src={image} alt="" /> 
         <div className={style.containerCardBody}>
            <button className={style.close} onClick={onClose}>X</button>
            <div className={style.containerName}>
            <Link to={`/detail/${id}`}>
            <h2 className={style.name}>{name}</h2>
            </Link>
            </div>
            <div className={style.containerCardFooter}>
            <h2 className={style.gender}>{gender}</h2>
            <h2 className={style.specie}>{species}</h2>
            </div>
         </div>
      </div>
   );
}

const mapDispatchToProps=(dispatch)=>{
   return {
      addFavorite: (character)=>{dispatch(addFavorite(character))}, //la action es la que est치 dentro del dispatch
      removeFavorite: (id)=>{dispatch(removeFavorite(id))} 
   }
}  //{newFavorite: fn, removeFavorite:fn}
/* 
const mapStateToProps=(state)=>{
   return{
      myFavorites: state.myFavorites
   }
} */

export default connect (null, mapDispatchToProps)(Card)/*  */
