import { useState, useEffect } from "react";
import "./App.css";
import Cards from "./components/cards/Cards.jsx";
import Nav from "./components/nav/Nav";
import About from "./components/about/About";
import Detail from "./components/detail/Detail";
import { Route, Routes, useNavigate, useLocation } from "react-router-dom";
import Form from "./components/form_/Form";

function App() {
  const [characters, setCharacters] = useState([]);

  //las props van siempre desde los padres a los hijos, en cambio, los eventos van desde los hijos a los padres. En searchBar puedo tener un evento cuya función esté en app
  const onSearch = (character) => {
    fetch(`https://rickandmortyapi.com/api/character/${character}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.name) {
          setCharacters((oldChars) => [...oldChars, data]);
        } else {
          window.alert("No hay personajes con ese ID");
        }
      });
  };

  const onClose = (id) => {
    setCharacters(characters.filter((character) => character.id !== id));
  };

  const navigate = useNavigate();

  const [access, setAccess] = useState(false);
  let username = "oc_005@hotmail.com";
  let password = "fulanito123";

  const login = (userData) => {
    if (userData.username === username && userData.password === password) {
      setAccess(true);
      navigate("/home");
    }
  };

  useEffect(() => {
    !access && navigate("/");
  }, [access]);

  const location = useLocation();

  return (
    <div className="App" style={{ padding: "25px" }}>
      {location.pathname === "/" ? <Form /> : <Nav onSearch={onSearch} />}
      <Routes>
        <Route
          path="home"
          element={<Cards characters={characters} onClose={onClose} />}
        />
        <Route path="about" element={<About />} />
        <Route path="detail/:detailId" element={<Detail />} />
      </Routes>
    </div>
  );
}

export default App;
