import {
  ADD_FAVORITE,
  DELETE_FAVORITE,
  DELETE_REPES,
  FILTER,
  ORDER,
} from "./types";

export const addFavorite = (character) => {
  return {
    type: ADD_FAVORITE,
    payload: character,
  };
};

export const deleteFavorite = (id) => {
  return {
    type: DELETE_FAVORITE,
    payload: id,
  };
};

export const filterCards = (gender) => {
  return {
    type: FILTER,
    payload: gender,
  };
};

export const orderCards = (id) => {
  return {
    type: ORDER,
    payload: id,
  };
};

export const deleteRepes = (id) => {
  return {
    type: DELETE_REPES,
    payload: id,
  };
};
