const { ADD_FAVORITE, DELETE_FAVORITE, FILTER } = require("./types");

const initialState = {
  myFavorites: [],
  allCharacters: [],
};

export const reducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case ADD_FAVORITE:
      return {
        ...state,
        myFavorites: [...state.allCharacters, payload],
        allCharacters: [...state.myFavorites], //me hago una copia de ese estado porque voy a estar filtrando los favoritos y no quiero pisar el estado original sino tener un respaldo el cual poder recuperar => necesitamos dos estados que manejen lo mismo, uno lo vamos a usar para hacer el filtrado sin pisar el original
      };
    case DELETE_FAVORITE:
      return {
        ...state,
        myFavorites: state.myFavorites.filter(
          // es mejor hacerlo en una variable aparte antes del return
          (favorite) => favorite.id !== payload
        ),
      };
    case FILTER:
      const { allCharacters } = state;
      const filterGender = allCharacters.filter((ch) => {
        ch.gender === payload;
      });
      return {
        ...state,
        myFavorites: filterGender,
      };
    case ORDER:
      const { allCharactersOrder } = state;
      if (payload === "Ascendente") {
        return {
          ...state,
          myFavorites: allCharactersOrder.sort((a, b) => a - b),
        };
      } else {
        return {
          ...state,
          myFavorites: allCharactersOrder.sort((a, b) => b - a),
        };
      }
    default:
      return {
        ...state,
      };
  }
};
